INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(187, 'Maratón Córdoba', 'Individual;Kids', 'Córdoba', 'Ciudad de Córdoba', 'Patio Olmos', '2015-05-03 00:00:00.0', 'En Mayo del 2015 la Ciudad de Córdoba está preparando una gran recepción a los Atletas que participarán en la “Maratón Córdoba”. La ciudad cuenta con hermosos parques, iglesias, el casco histórico, la costanera, la Cañada, el Estadio Kempes; todos ellos incluidos en el recorrido de la “Maratón Córdoba”. La propuesta es una Maratón de Música y Tecnología, representada esta última por los entretenimientos en la Expo, juegos interactivos, transmisión en vivo de la largada y llegada, y puestos de autoconsulta del tiempo inmediatamente al arribo a la meta. La música será dispuesta a lo largo del recorrido de la “Maratón Córdoba”, con veinte grupos musicales que acompañarán a los corredores y al público que seguirá el evento.', 'http://maratondecordoba.com.ar/w/class/42k/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xft1/v/t1.0-9/10645149_1515271518754550_4742285474871287121_n.png?oh=7be7f679c6a606981d25ff0357ba157a&oe=564629CE', '3/10/42')
GO
INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(188, 'Media Maraton del Glaciar', 'Individual', 'Santa Cruz', 'El Calafate', 'Parque Nacional Los Glaciares', '2015-04-11 00:00:00.0', 'La Media Maratón del Glaciar, es un evento del tipo deportivo en la modalidad Pedestrismo o Running. Participarán del evento deportistas amateurs y profesionales de diferentes regiones argentinas y de otros países, motivados por el hecho de correr en un lugar de tan alto reconocimiento turístico, como es el Parque Nacional los Glaciares. Las modalidades de 21K y 10K formarán parte de este evento que tiene como principal desafío transitar al aire libre por este único y espectacular paisaje. La totalidad de ambos recorridos se realiza sobre el asfalto.', 'http://maratondelglaciar.com/', false, 'https://fbcdn-sphotos-g-a.akamaihd.net/hphotos-ak-xfa1/v/t1.0-9/10931456_779309118771077_4356585760588004843_n.jpg?oh=3f38f84d8636baf9773628e5f515b810&oe=565AC86C&__gda__=1448595802_4167bb3aafdeb4f8e584adc15edc2e22', '10/21')
