INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(189, 'Llao Llao 21k', 'Individual', 'Río Negro', 'Bariloche', 'Hotel Llao Llao', '2015-12-19 00:00:00.0', 'Llao Llao 21K, prueba atlética en las modalidades Media Maratón 21K y prueba de 10K. se desarrolla íntegramente dentro del Parque Municipal Llao Llao. El punto de partida y llegada será el predio del Hotel Llao Llao, y recorrerá la ruta en dirección hacía Bahía López, desde donde se realiza el retorno primero para la modalidad 10K. El recorrido de 21K continúa por la ruta, íntegramente por el asfalto, hasta cruzar el arroyo López y se retorna desde el desvío de ripio hacía Colonia Suiza, sobre la misma ruta. El recorrido presenta desniveles moderados.', 'www.llaollao21k.com', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xtf1/v/t1.0-9/10426741_878112785546993_4167136009246866939_n.jpg?oh=d4d0e99912c5f058fb3e54e60e477898&oe=5646B1F0', '10/21')
