INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(200, 'K21 Series Junin', 'Individual', 'Buenos Aires', 'Junin', 'Parque Natural Laguna de Gómez', '2015-07-12 00:00:00.0', 'Con largada y llegada en el Parque Natural Laguna de Gómez, esta etapa nos lleva al principal atractivo turístico lacustre de Buenos Aires, con un entorno que comprende alrededor de 200 ha mayormente arbolada. Con las características de las etapas bonaerenses, se trata de un circuito ligero, de cross, con partes de costering, atravesando bañados y montes de vegetación autóctona.', 'http://k21series.com/junin/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
