INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(201, 'K21 Series San Juan', 'Individual', 'San Juan', 'San Juan', 'Punta Tabasco, Dique Ullum', '2015-07-26 00:00:00.0', 'A orillas del Dique Ullun, en medio de una región predominantemente árida, como un oasis aparece la represa de aguas azules, apenas 18 km distante de la ciudad de San Juan. Carrera preponderantemente técnica, con ascenso a cerros que permiten una panorámica de todo el entorno, incluso del valle de Tulúm. El recorrido los llevará con largada y llegada en Punta Tabasco, por el perilago del embalse.', 'http://k21series.com/san-juan/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
GO
INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(202, 'K21 Series Parana', 'Individual', 'Entre Ríos', 'Parana', '', '2015-08-02 00:00:00.0', 'La capital de Entre Ríos será nuevamente sede de la etapa mesopotámica de K21, con un suelo de carácter ondulado, resavio de las cuchillas correntinas, no posee lagos pero sin embargo está bañada por las aguas de río Paraná y Uruguay. Siguiendo el tópico de las etapas de la región pampeana, se trata de un trazado que permite correr prácticamente todo el tiempo , siendo los mayores obstáculos los bañados y zonas que suelen invadirse por el agua, a la vez que posee una vegetación exuberante propia de un clima subtropical. La accesibilidad, los atractivos para pasar un día de disfrute en familia la hacen una excelente propuesta.', 'http://k21series.com/parana/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
GO
INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(203, 'K21 Series La Cumbrecita', 'Individual', 'Córdoba', 'La Cumbrecita', 'Puente Peatonal de La Cumbrecita', '2015-08-22 00:00:00.0', 'Una cálida villa de estilo europeo, enclavada en la zona de sierras cordobesas que reúne todas las bondades de esta región. Buen clima, terreno con variedad de posibilidades, trepadas y descensos técnicos, visuales maravillosas, tupidos bosques de coníferas. La permanente vigilancia del vuelo de los cóndores, hacen de esta una de las etapas más elegidas por los amantes del trail run que buscan la contención de una auténtica aldea de montaña, junto a un desafío a las propias condiciones.', 'http://k21series.com/la-cumbrecita/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
GO
