INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(204, 'K21 Series Villa Pehuenia', 'Individual', 'Neuquen', 'Villa Pehuenia', 'Frente a Biblioteca Maestro Galeano', '2015-08-30 00:00:00.0', 'La etapa Villa Pehuenia, nos viene premiando con su manto de nieve blanquísima en un 60 % del recorrido por cerros, con las aguas cristalinas de los lagos Moquehue y Aluminé. Joven villa de montaña, contenida por bosques milenarios de araucarias, se llena de magia y magestuoso atractivo. La tercera edición en tierras neuquinas fortaleciendo el contacto con la naturaleza en su estado más puro.', 'http://k21series.com/villa-pehuenia/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
