INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(205, 'K21 Series Avellaneda', 'Individual', 'Santa Fe', 'Avellaneda', '', '2015-09-19 00:00:00.0', 'Novedad en el circuito 2014, a 5 km de Reconquista, con senderos marcados por los bañados y sector literalmente invadidos por la vegetación, Avellaneda será nuevamente referencia en el noreste santafecino. Si bien la altimetría no es un factor para nada determinante, los obstáculos naturales, completan la verdadera esencia del espíritu de kseries.', 'http://k21series.com/avellaneda/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
