INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(206, 'K21 Series San Rafael', 'Individual', 'San Rafael', 'Mendoza', '', '2015-09-27 00:00:00.0', 'Sexta oportunidad en que K21 llega al Cuyo, y como es ya un clásico a la ciudad de San Rafael, que está rodeada de oportunidades para redescubrir senderos y paisajes propicios para el desafío. San Rafael, una vez más, como referente de las disciplinas que elevan la adrenalina, los espera.', 'http://k21series.com/san-rafael/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
