INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(207, 'K21 Series San Pedro', 'Individual', 'Buenos Aires', 'San Pedro', '', '2015-10-03 00:00:00.0', 'Nueva edición de K21 San Pedro, en el bonito entorno de las barrancas del río Paraná, divertido circuito por senderos que se internan el monte, caminos costeros y paisaje campestre. Concentrando la actividad en un predio que contextualiza lo que seguramente será una jornada de compartir con los corredores y acompañantes.', 'http://k21series.com/san-pedro/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
