INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(208, 'K21 Series San Martin de los Andes', 'Individual', 'Neuquen', 'San Martin de los Andes', 'El Desafío Mountain Resort, Ruta 40 – S.M. de los Andes', '2015-10-11 00:00:00.0', 'Primavera en la cordillera, y que mejor marco que San Martín de los Andes, en un circuito que combina las bondades de la naturaleza, los andes como contexto, bosques de coníferas, caminos que ascienden a las mejores visuales. Ha sido hasta ahora una perlita dentro del circuito, con algunas novedades en el recorrido que seguirán sumando atractivos.', 'http://k21series.com/san-martin-de-los-andes/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
