INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(209, 'K21 Series Lujan', 'Individual', 'Buenos Aires', 'Lujan', '', '2015-10-17 00:00:00.0', 'Atravesada por el río Lujan, la ciudad de la fe, es reconocida mundialmente por la imponente Basílica gótica. Rodeada de estancias y lugares que se prestan al descanso y al contacto con una naturaleza que da el marco adecuado para una nueva etapa de K21. Su trazado contemplará tocar los puntos más característicos de esta maravillosa ciudad, deporte y turismo cultural serán la clave del encuentro.', 'http://k21series.com/lujan/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
GO
INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(210, 'K21 Series Mar del Plata', 'Individual', 'Buenos Aires', 'Mar del Plata', 'Balneario 15, Apolo de Punta Mogotes', '2015-03-01 00:00:00.0', 'La perla del atlántico afianza la etapa de mar, rescatando los puntos turísticos más reconocidos, K21 suma a su favor, tramos de carrera por playa y médanos; sectores ligeros que los llevarán a internarse literalmente en el mar, en el espigón que regala la visual del puerto. Un circuito con la dureza que da la arena combinando kilómetros ágiles de carrera a campo través. Este año, con la modificación de fecha, se acerca a los últimos vestigios de verano para mantener además la posibilidad de disfrutar de la playa.', 'http://k21series.com/mdq/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
