INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(212, 'K21 Series Ischigualasto', 'Individual', 'San Juan', 'Ischigualasto', 'Parque Nacional Ischigualasto', '2015-03-15 00:00:00.0', 'Etapa nueva en la serie, recorre el cordón montañoso del Valle Fértil, donde se sitúa el Parque Nacional Ischigualasto, región árida en la que la erosión labró formas singulares con arenisca de color rojo ladrillo y otros que la naturaleza nos obsequia. Con acantilados de hasta 200 mts de altura, combinada con cañadones y gargantas por donde corren arroyos temporarios. Su espectacular paisaje llevó a denominarlo valle de la luna o paisaje de otro mundo. Sin duda, será una de las estrellas de la edición 2015', 'http://k21series.com/ischigualasto/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
