INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(213, 'K21 Series Rosario', 'Individual', 'Santa Fe', 'Rosario', 'Campo Timbo', '2015-03-21 00:00:00.0', 'A 40 km de Rosario, con el atractivo propio del campo santafesino, una etapa por demás instalada en el calendario de los corredores, por su fácil acceso, por tratarse de un circuito entretenido, desarrollado a la vera del río Carcarañá, recorriendo la frondosa vegetación del añoso bosque de eucaliptos colorados y especies autóctonas, un cross rápido por sectores y otro tanto divertido.', 'http://k21series.com/rosario/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
