INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(214, 'K21 Series Colon', 'Individual', 'Entre Ríos', 'Colon', 'Desde Cámping “Los Tilos”, camino costero sur S/N.', '2015-04-12 00:00:00.0', 'Correr en las inmediaciones del río Uruguay, su delta, con arroyos y bañados. La vegetación caracterizada por la selva en galería –derivación final de la selva misionera, con lapachos, laurel y seibos; sectores con suelos pantanosos y las cuchillas características hacen de K21 Colon una etapa variada, con el agua como elemento preponderante que se completa recorriendo kilómetros de playas de arena fina .', 'http://k21series.com/colon/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
