INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(215, 'K21 Series Areco', 'Individual', 'Buenos Aires', 'San Antonio de Areco', 'Parque Criollo', '2015-05-03 00:00:00.0', 'Nuevo destino, a 110 km de Buenos Aires, incursionando en San Antonio de Areco una de las ciudades más antiguas y características del campo argentino. A la vera del río Areco, con un entorno que deslumbra por la calidad de sus producciones artesanales, el santuario Gaucho también ofrecerá completar una jornada de disfrute de naturaleza y gastronomía.', 'http://k21series.com/areco/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
