INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(216, 'K21 Series Neuquen', 'Individual', 'Neuquen', 'Villa El Chocon', 'Gimnasio Municipal.', '2015-05-24 00:00:00.0', 'A 80 km de Neuquén Capital, con el Lago Ramos Mexía y un terreno caracterizado por su suelo arcilloso, típico paisaje de la estepa patagónica, el circuito se desarrolla por senderos que atraviesan la meseta, bordeando el río Limay con la permanente panorámica del espejo azul del lago. En los suelos desérticos que caracterizan el entorno de Villa El Chocon, se encontraron los restos del dinosaurio carnívoro considerado el más grande descubierto, que 100 millones de años atrás, recorría la región. Esta será la segunda oportunidad de desandar sus senderos, K21 EL CHOCON, inicio de la edición 2015 de la Copa Optitech K21 Salomon, les da la bienvenida!', 'http://k21series.com/neuquen/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
GO
INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(217, 'K21 Series San Nicolas', 'Individual', 'Buenos Aires', 'San Nicolas', 'Parque Rafael de Aguiar', '2015-05-31 00:00:00.0', 'Mística y natural podríamos definir a la etapa de San Nicolás, circuito pleno de paisajes cautivantes, a orillas del río Paraná, ideal para integrar el costering, internarse en los cañaverales y divertirse en los montes de vegetación autóctona, las infaltables barrancas completan una propuesta que se consolida.', 'http://k21series.com/san-nicolas/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
GO
INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(218, 'K21 Series San Lorenzo', 'Individual', 'Salta', 'San Lorenzo', 'El Fortín calle Mariano Moreno al pie del cerro', '2015-06-14 00:00:00.0', 'Sosteniendo el recorrido elegido en el 2014, se busca seguir mostrando los mejores atractivos de la zona teniendo unas excelentes vistas de San Lorenzo, tanto del Este como del Oeste, mostrando sus distintas vegetaciones, las Yungas, la imponente y frondosa avenida de acceso y como su nombre lo dice la famosa Loma Balcón y cambiando el lugar de Largada y llegada a los 1.438 m.s.n.m. se busca ubicarse con una muy linda vista al pie del cerro y hacer una carrera más DURA y un poco más lerda que la anterior, los que participen de la opción de 21 Km en sus últimos kilómetros tendrán que llegar a los 1.996 m.s.n.m. y los de 11 Km tendrán su altura máxima en la llegada.', 'http://k21series.com/san-lorenzo/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
GO
INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(219, 'K21 Series Tandil', 'Individual', 'Buenos Aires', 'Tandil', 'Polideportivo Municipal y Espacio Espiritual Huella Mapuche', '2015-06-21 00:00:00.0', 'Tan sólo su nombre representa un ícono de la actividad al aire libre, los encantos de esta región tan accesible desde las grandes urbes, con una historia deportiva incuestionable, con todos los aditamentos para ser la vedete de la serie. Su trazado lleva a transitar las sierras más antiguas del planeta, los atractivos turísticos de una ciudad preparada para la aventura, garantizando los elementos indispensables de una gran prueba de cross country.', 'http://k21series.com/tandil/', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xta1/v/t1.0-9/10920903_620768081288774_1091139458042730843_n.jpg?oh=db78896385392b3628f5d8b750d1c2e5&oe=56575807', '10/21')
