INSERT INTO public.carrera(id, nombre, modalidades, provincia, ciudad, direccion, fecha_inicio, descripcion, url_web, recomendada, url_imagen, distancia_disponible)
  VALUES(279, 'Tandil Ultra Trail 2015', 'Individual', 'Buenos Aires', 'Tandil', 'Posta Natural - Zona "La Elena"', '2015-08-23 00:00:00.0', 'El frío de Agosto te traerá la oportunidad de "CORRER TUS LIMITES" en el TANDIL ULTRA TRAIL 2015. Esta será una prueba de cross trail a tu medida en las sierras más antiguas del planeta, que se encuentran en Tandil, provincia de Buenos Aires. Las pendientes permanentes en los caminos y sendas serranas serán las constantes del trayecto que te llevará por los lugares más destacados de nuestro entorno. Esta prueba, llamada a convertirse en un clásico del trail, te propone tres diferentes distancias: 11k, 30k y 50k que te permitirán elegir el desafío a tu medida. Con la salida del sol del 23 de Agosto se iniciará la segunda edición de una competencia que pondrá a prueba tus límites. ¿Sos capaz de correrlos?', 'http://www.perfilextremo.com/ultratrail.html', false, 'https://scontent-mia1-1.xx.fbcdn.net/hphotos-xfa1/v/t1.0-9/18791_1056894550988668_1561016540801809766_n.jpg?oh=5096d6171ac040b5fb16c3d1145512e3&oe=5654F32C', '11/30/50')
